[
{
"claim": "Gambling addiction can produce anxiety, impulsive behavior, and physiological responses similar to those of substance addiction."
},
{
"claim": "Australia’s mainstream news and sports broadcasts routinely include betting odds, contributing to the normalization of gambling."
},
{
"claim": "Frequent public exposure to gambling promotions encourages acceptance of betting as part of everyday entertainment."
},
{
"claim": "Problem gambling often begins with small wagers and can escalate rapidly to large financial losses."
},
{
"claim": "Education about the psychological mechanisms of addiction is essential to reduce gambling-related harm."
},
{
"claim": "Allowing legal gambling while promoting awareness of its risks supports personal freedom and responsibility."
},
{
"claim": "Gambling apps intensify addiction by merging financial risk with the instant feedback loops of mobile technology."
},
{
"claim": "Smartphone use provides constant dopamine stimulation, reinforcing compulsive checking behaviors across multiple apps."
},
{
"claim": "Social media platforms use algorithmic personalization to maximize user engagement and prolong screen time."
},
{
"claim": "Excessive digital activity can contribute to widespread psychological fatigue and difficulty focusing."
},
{
"claim": "Overwork and continuous production schedules can lead to cognitive exhaustion and reduced creativity."
},
{
"claim": "Extended time spent in natural environments supports psychological restoration and recovery from stress."
}
]

